import { Component } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { getUserId } from 'src/app/auth/state/auth.selector';
import { AppState } from 'src/app/store/app.state';
import { setLoadingSpinner } from 'src/app/store/shared/shared.actions';
import { getErrorMessage } from 'src/app/store/shared/shared.selector';
import { createUrl } from '../../state/url.actions';

@Component({
  selector: 'app-short-url',
  templateUrl: './short-url.component.html',
  styleUrls: ['./short-url.component.css'],
})
export class ShortURLComponent {
  errorMessage: Observable<string>;
  userId: number = 0;

  constructor(private store: Store<AppState>) {
    this.errorMessage = this.store.select(getErrorMessage);
    this.store.select(getUserId).subscribe(result => this.userId = result)
  }

  Add(originalUrl: string) {
    this.store.dispatch(setLoadingSpinner({ status: true }));
    this.store.dispatch(createUrl({ originalUrl, userId: this.userId }));
  }
}
<div class="alert alert-danger" *ngIf="errorMessage | async as error">
  {{ error }}
</div>
<div class="row">
  <div class="col-md-6 offset-md-3">
    <h1>URL shorter</h1>
    <hr />
    <form #urlForm="ngForm" (ngSubmit)="Add(urlForm.value)">
      <div class="mb-3">
        <input
          type="url"
          ngModel
          name="originalUrl"
          class="form-control"
          require
          #url="ngModel"
          placeholder="Entre URL"
        />
        <span class="error" *ngIf="url.errors?.required && url.touched"
          >URL is required</span
        >
        <span class="error" *ngIf="url.errors?.url && url.touched"
          >URL is invalid</span
        >
      </div>
      <div class="d-grid gap-2">
        <button type="submit" class="btn btn-primary" [disabled]="urlForm.invalid">Short</button>
      </div>
    </form>
  </div>
</div>
