import { Injectable } from '@angular/core';
import { Url } from '../pages/all-urls/models/url';



@Injectable({ providedIn: 'root' })
export class UrlService {
  private storageInitialised = false;

  constructor(private storage: Storage) {}

  async getAll(): Promise<Url[]> {
    if (!this.storageInitialised) await this.storage.create();

    return (await this.storage.get('urls')) || [];
  }

  async save(todos: Url[]) {
    if (!this.storageInitialised) await this.storage.create();

    return this.storage.set('urls', todos);
  }
}
