import { Component, OnInit } from '@angular/core';
import { Url } from '../../../global/models/url';
import { Observable } from 'rxjs';
import { Store } from '@ngrx/store';
import { AppState } from 'src/app/store/app.state';
import { getUrls } from 'src/app/url/state/url.selector';
import { deleteUrl, loadUrls } from 'src/app/url/state/url.actions';
import {
  isAdmin,
  isAuthenticated,
  isMyUrl,
} from 'src/app/auth/state/auth.selector';
import { setLoadingSpinner } from 'src/app/store/shared/shared.actions';

@Component({
  selector: 'app-all-urls',
  templateUrl: './all-urls.component.html',
})
export class AllUrlsComponent implements OnInit {
  urls?: Observable<Url[]>;
  isAdmin: Observable<boolean>;

  constructor(private store: Store<AppState>) {
    this.isAdmin = this.store.select(isAdmin);
    this.store.dispatch(setLoadingSpinner({ status: true }));
  }

  ngOnInit() {
    this.urls = this.store.select(getUrls);
    this.store.dispatch(loadUrls());
  }

  delete(url: Url) {
    const id: number = url.id;
    this.store.dispatch(deleteUrl({ id }));
  }

  showButton(url: Url) {
    let check = false;
    this.store.select(isAdmin).subscribe((result) => (check = result));

    if (!check) return this.store.select(isMyUrl(url.createdByUserName));

    return this.store.select(isAdmin)

    //return this.store.select(isMyUrl(url.createdByUserName));
  }
}
<h1>All URLs</h1>
<hr />
<table class="table table-striped" aria-labelledby="tableLabel" *ngIf="urls">
  <thead>
    <tr>
      <th>URL</th>
      <th>Shorted URL</th>
      <th>Action</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let url of urls | async">
      <td>{{ url.originalUrl }}</td>
      <td>{{ url.shortUrl }}</td>
      <td>
        <button
          type="button"
          class="btn btn-info btn-sm"
          routerLink="/url-info"
          [state]="url"
        >
          Info</button
        ><button
          type="button"
          class="btn btn-danger btn-sm"
          *ngIf="showButton(url) | async"
          type="button"
          (click)="delete(url)"
        >
          Delete
        </button>
      </td>
    </tr>
  </tbody>
</table>
