import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from '../../../environments/environment';

@Component({
  selector: 'app-short-url',
  templateUrl: './short-url.component.html',
})
export class ShortURLComponent {
  constructor(private http: HttpClient) {}

  async Add(OriginalUrl: string) {
    return this.http
      .post(environment.Url + 'url/create', OriginalUrl)
      .subscribe();
  }
}
<h1 id="tableLabel">URL shorter</h1>

<form #urlForm="ngForm">
  <input type="text" ngModel name="OriginalUrl" placeholder="Entre URL">
  <button routerLink="/url-info" [state]="urlForm.value" (click)="Add(urlForm.value)">Short</button>
</form>
