import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/environments/environment';
import { Store } from '@ngrx/store';
import { getAllUrls } from 'src/app/state/urls/url.selectors';
import { loadUrls } from 'src/app/state/urls/url.actions';
import { AppState } from 'src/app/state/app.state';

@Component({
  selector: 'app-all-urls',
  templateUrl: './all-urls.component.html',
})
export class AllUrlsComponent implements OnInit {
  public urls$ = this.store.select(getAllUrls);

  constructor(private store: Store<AppState>) {
  }
  ngOnInit() {
    this.store.dispatch(loadUrls());
  }
}
<h1>All URLs</h1>

<table class="table table-striped" aria-labelledby="tableLabel" *ngIf="urls$">
  <thead>
    <tr>
      <th>URL</th>
      <th>Shorted URL</th>
      <th>Created date</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let url of urls$ | async">
      <td>{{ url.originalUrl }}</td>
      <td>{{ url.shortUrl }}</td>
      <td>{{ url.createdAt }}</td>
    </tr>
  </tbody>
</table>
